sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord()|| newValue === '') {
      return;
   }

	var grr = new GlideAjax('GetEmailAddress'); //to call the script include
	grr.addParam('sysparm_name','details'); //to call the function of script include
	grr.addParam('sysparm_userid',newValue); //to get the current value of requested for and passed into script include
	grr.getXML(getemail);
	function getemail(response){
		var ans = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
    g_form.setValue('u_requested_for_email',ans);
  }
	}

   //Type appropriate comment here, and begin script below
